// 3714. Longest Balanced Substring II
// https://leetcode.com/problems/longest-balanced-substring-ii/

struct Solution;

impl Solution {
    pub fn longest_balanced(s: String) -> i32 {
        let s = s.as_bytes();
        let n = s.len();
        let mut max_len = 0;

        // Try all possible substrings
        for i in 0..n {
            let mut freq = [0i32; 26];
            let mut freq_count = [0i32; 20001]; // freq_count[f] = how many chars have frequency f
            let mut unique_chars = 0;

            for j in i..n {
                let idx = (s[j] - b'a') as usize;

                let old_freq = freq[idx];
                if old_freq > 0 {
                    freq_count[old_freq as usize] -= 1;
                }

                freq[idx] += 1;
                let new_freq = freq[idx];
                freq_count[new_freq as usize] += 1;

                if old_freq == 0 {
                    unique_chars += 1;
                }

                // Balanced if all unique_chars have the same frequency
                // This means freq_count[some_f] == unique_chars for exactly one f
                if freq_count[new_freq as usize] == unique_chars {
                    let len = j - i + 1;
                    max_len = max_len.max(len);
                }
            }
        }

        max_len as i32
    }
}

#[cfg(test)]
mod tests {
    use crate::medium::longest_balanced_substring_ii::Solution;

    #[test]
    fn test_longest_balanced_1() {
        let s = "abbac".to_string();
        assert_eq!(4, Solution::longest_balanced(s));
    }

    #[test]
    fn test_longest_balanced_2() {
        let s = "aabcc".to_string();
        assert_eq!(3, Solution::longest_balanced(s));
    }

    #[test]
    fn test_longest_balanced_3() {
        let s = "aba".to_string();
        assert_eq!(2, Solution::longest_balanced(s));
    }

    #[test]
    fn test_longest_balanced_4() {
        let s = "".to_string();
        assert_eq!(15828, Solution::longest_balanced(s));
    }
}
